INSERT INTO public.advices (id, question, answer) VALUES (1, '¿Por qué es importante investigar la empresa antes de aplicar?', 'Conocer la empresa te ayuda a entender su cultura, valores y expectativas, lo que aumenta tus posibilidades de destacar en una entrevista.');
INSERT INTO public.advices (id, question, answer) VALUES (2, '¿Cómo hacer que tu CV destaque entre los demás?', 'Destaca logros concretos, usa palabras clave de la oferta y adapta tu CV a cada empresa.');
INSERT INTO public.advices (id, question, answer) VALUES (3, '¿Qué debo incluir en mi carta de presentación?', 'Debes explicar por qué te interesa la empresa y cómo tus habilidades encajan con el puesto.');
INSERT INTO public.advices (id, question, answer) VALUES (4, '¿Cómo prepararse para una entrevista técnica?', 'Practica problemas comunes, repasa estructuras de datos y algoritmos, y realiza simulaciones de entrevistas.');
INSERT INTO public.advices (id, question, answer) VALUES (5, '¿Cuál es la mejor manera de responder preguntas difíciles en una entrevista?', 'Mantén la calma, sé honesto y enfoca tu respuesta en lo que has aprendido o cómo resolverías el problema en el futuro.');
INSERT INTO public.advices (id, question, answer) VALUES (6, '¿Qué hacer después de una entrevista de trabajo?', 'Envía un correo de agradecimiento, reflexiona sobre tu desempeño y prepárate para los siguientes pasos del proceso.');
